import { S } from "@shared/modules";

export {};

const numDifferentIntegers = (word: string): number =>
  [...new Set(word.match(/\d+/g)?.map((s) => s.replace(/^0+/, '')) ?? [])].length;

describe("num different integers", () => {
  it("case 1", () => {
    expect(numDifferentIntegers("a123bc34d8ef34")).toBe(3);
  });
  it("case 2", () => {
    expect(numDifferentIntegers("leet1234code234")).toBe(2);
  });
  it("case 3", () => {
    expect(numDifferentIntegers("a1b01c001")).toBe(1);
  });
  it("case 4", () => {
    expect(numDifferentIntegers("a1b01c0")).toBe(2);
  });
  it("case 5", () => {
    expect(numDifferentIntegers("0a0")).toBe(1);
  });
  it("case 6", () => {
    expect(
      numDifferentIntegers(
        "",
      ),
    ).toBe(3);
  });
});
